import{_ as t,c as a,o,j as e,a as s}from"./chunks/framework.DRsG23AI.js";const P=JSON.parse('{"title":"metaPhorestはについて","description":"","frontmatter":{"layout":"doc","outline":"deep"},"headers":[],"relativePath":"about/index.md","filePath":"about/index.md","lastUpdated":null}'),r={name:"about/index.md"},n=e("h1",{id:"metaphorestはについて",tabindex:"-1"},[s("metaPhorestはについて "),e("a",{class:"header-anchor",href:"#metaphorestはについて","aria-label":'Permalink to "metaPhorestはについて"'},"​")],-1),d=e("p",null,"metaPhorestは，生命科学や生命論の展開を参照しつつ，「生命」を巡る美学・芸術（バ イオアート，バイオメディア・アート，バイオロジカルアート，生命美学）の実験・研究 ・制作を行うためのプラットフォームです。2007年から，早稲田大学先端生命医科学研 究施設の基礎生命科学の研究室内に設置されています。生命に関わる表現に興味を持つア ーティストやデザイナーが比較的長期に亘って滞在し，生命科学研究の現場で，実験設備 やセミナーなどを科学者たちと共有しながら活動するための機会を提供しています。 生命科学は「生命とは何か」という問を巡る学問領域ですが，その問は古来，哲学，美 学，倫理学，文学，芸術の対象であり続けてきました。私たちは，生命科学の知見や技術 を現代的な芸術表現で参照しつつ，それを通じて生命や科学と芸術の関係性や，それらの 社会的・思想的・文化的な基盤について多面的に探究することを重視しています。また， 生物学の知見や，近年の生物工学の進展により，芸術やデザインの表現にも新たな展開が 生まれており，こうした新たな芸術表現を実践・研究することも大きな関心を持ってお り，国内外のバイオメディア・アーティストや研究者とも積極的に交流しています。な お，科学研究の理解・支援増進のための広報・デザインの受託などは行っていません。 レジデンス・アーティストを随時受け付けています。お気軽にお問い合わせください。 “metaPhorest”という名称には，metaphor + forest, meta-forest, super-metaphorといった複合的な含意が 込められています。",-1),i=e("p",null,"Hideo Iwasaki, Director",-1),c=[n,d,i];function l(h,m,p,_,u,f){return o(),a("div",null,c)}const b=t(r,[["render",l]]);export{P as __pageData,b as default};
