import{_ as t,c as i,o as a,j as e,a as o}from"./chunks/framework.CPu_Kgir.js";const v=JSON.parse('{"title":"About metaPhorest","description":"","frontmatter":{"layout":"doc","outline":"deep"},"headers":[],"relativePath":"en/about/index.md","filePath":"en/about/index.md","lastUpdated":1726753088000}'),s={name:"en/about/index.md"},n=e("h1",{id:"about-metaphorest",tabindex:"-1"},[o("About metaPhorest "),e("a",{class:"header-anchor",href:"#about-metaphorest","aria-label":'Permalink to "About metaPhorest"'},"​")],-1),r=e("p",null,"metaPhorest is a practical platform for artistic researches on biological/biomedia art and bioaesthetics, located within a biological laboratory at Center for Advanced Biomedical Sciences, Waseda University. We provide relatively long-term (more than several months) artist-in-residence program for those who are interested in biology-inspired/aided/criticizing artistic activity, sharing biological facility, discussion, seminars with life scientists.",-1),c=e("p",null,"Biological science is an academic discipline researching on “what life is”, which has also been profoundly studied in the fields of philosophy, aesthetics, literature, ethics, and art, etc. We aim multidisciplinary pursues of relationship between art and science, and philosophical, aesthetic, socio-cultural implication of life and biology via artistic studies and practice. We are also interested in the aesthetic context in which biological science/ technology have affected contemporary art/design, and collaborating with many domestic/ foreign biomedia artists and researchers. Please note that we do not aim scientific advertisement activities.",-1),l=e("p",null,"We accept residence artists. Please do not hesitate to ask us for more info.",-1),d=e("p",null,"Hideo Iwasaki, Director",-1),h=[n,r,c,l,d];function p(m,u,f,b,_,g){return a(),i("div",null,h)}const w=t(s,[["render",p]]);export{v as __pageData,w as default};
